import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [cards, setCards] = useState([]);
  const [newCard, setNewCard] = useState({ Question: '', Answer: '' });
  const [editingCard, setEditingCard] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [flipped, setFlipped] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const cardsPerPage = 3;

  useEffect(() => {
    axios.get('http://localhost:5000/cards')
      .then(response => {
        setCards(response.data);
        setFlipped(new Array(response.data.length).fill(false));
      })
      .catch(error => {
        console.error('There was an error fetching the cards!', error);
      });
  }, []);

  const handleAddCard = () => {
    axios.post('http://localhost:5000/cards', newCard)
      .then(response => {
        setCards([...cards, { ...newCard, id: response.data.id }]);
        setNewCard({ Question: '', Answer: '' });
        setShowAddForm(false);
        setCurrentPage(Math.ceil((cards.length + 1) / cardsPerPage) - 1); // Move to the last page after adding a card
      })
      .catch(error => {
        console.error('There was an error adding the card!', error);
      });
  };

  const handleDeleteCard = (id) => {
    axios.delete(`http://localhost:5000/cards/${id}`)
      .then(() => {
        setCards(cards.filter(card => card.id !== id));
        // Adjust currentPage if necessary
        if (cards.length % cardsPerPage === 0 && currentPage > 0) {
          setCurrentPage(currentPage - 1);
        }
      })
      .catch(error => {
        console.error('There was an error deleting the card!', error);
      });
  };

  const handleEditCard = () => {
    axios.put(`http://localhost:5000/cards/${editingCard.id}`, editingCard)
      .then(() => {
        setCards(cards.map(card => (card.id === editingCard.id ? editingCard : card)));
        setEditingCard(null);
      })
      .catch(error => {
        console.error('There was an error updating the card!', error);
      });
  };

  const handleFlip = (index) => {
    const newFlipped = [...flipped];
    newFlipped[index] = !newFlipped[index];
    setFlipped(newFlipped);
  };

  const handlePrevious = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNext = () => {
    if ((currentPage + 1) * cardsPerPage < cards.length) {
      setCurrentPage(currentPage + 1);
    }
  };

  const displayedCards = cards.slice(currentPage * cardsPerPage, (currentPage + 1) * cardsPerPage);

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      <div className="card-container">
        {displayedCards.map((card, index) => (
          <div
            key={card.id}
            className={`card ${flipped[index + currentPage * cardsPerPage] ? 'flipped' : ''}`}
            onClick={() => handleFlip(index + currentPage * cardsPerPage)}
          >
            <div className="card-inner">
              <div className="card-front">
                <h2>{card.Question}</h2>
              </div>
              <div className="card-back">
                <h2>{card.Answer}</h2>
              </div>
            </div>
            <div className="card-actions">
              <button className="delete-button" onClick={(e) => {
                e.stopPropagation();
                handleDeleteCard(card.id);
              }}>Delete</button>
              <button className="edit-button" onClick={(e) => {
                e.stopPropagation();
                setEditingCard(card);
              }}>Edit</button>
            </div>
            {editingCard && editingCard.id === card.id && (
              <div className="edit-form">
                <input
                  type="text"
                  value={editingCard.Question}
                  onChange={e => setEditingCard({ ...editingCard, Question: e.target.value })}
                />
                <input
                  type="text"
                  value={editingCard.Answer}
                  onChange={e => setEditingCard({ ...editingCard, Answer: e.target.value })}
                />
                <button onClick={handleEditCard}>Save Changes</button>
                <button onClick={() => setEditingCard(null)}>Cancel</button>
              </div>
            )}
          </div>
        ))}
      </div>
      <div className="pagination">
        <button className="pagination-button" onClick={handlePrevious} disabled={currentPage === 0}>Previous</button>
        <button className="pagination-button" onClick={handleNext} disabled={(currentPage + 1) * cardsPerPage >= cards.length}>Next</button>
      </div>
      <div className="add-card">
        {!showAddForm ? (
          <button className="add-card-button" onClick={() => setShowAddForm(true)}>Add New Card</button>
        ) : (
          <div className="add-card-form">
            <h2>Add New Card</h2>
            <input
              type="text"
              placeholder="Question"
              value={newCard.Question}
              onChange={e => setNewCard({ ...newCard, Question: e.target.value })}
            />
            <input
              type="text"
              placeholder="Answer"
              value={newCard.Answer}
              onChange={e => setNewCard({ ...newCard, Answer: e.target.value })}
            />
            <button onClick={handleAddCard}>Add Card</button>
            <button onClick={() => setShowAddForm(false)}>Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
