import React, { useState } from 'react';
import './CardStyle.css';

const CardComponent = () => {
  const [flipped, setFlipped] = useState([false, false, false]);

  const handleFlip = (index) => {
    const newFlipped = [...flipped];
    newFlipped[index] = !newFlipped[index];
    setFlipped(newFlipped);
  };

  const cardsContent = [
    {
      front: { title: "What is ML?", text: "Name any 2 ML algorithms" },
      back: {
        title: "Answer:",
        text: "Machine learning is the process of teaching a machine repeatedly by training it. Two ML algorithms are Naive Bayes and KNN.",
      },
    },
    {
      front: { title: "What is React?", text: "Name any 2 React hooks" },
      back: {
        title: "Answer:",
        text: "React is a JavaScript library for building user interfaces. Two React hooks are useState and useEffect.",
      },
    },
    {
      front: { title: "What is Python?", text: "Name any 2 Python frameworks" },
      back: {
        title: "Answer:",
        text: "Python is a high-level programming language. Two Python frameworks are Django and Flask.",
      },
    },
  ];

  return (
    <div className="card-container">
      {cardsContent.map((content, index) => (
        <div
          key={index}
          className={`card ${flipped[index] ? 'flipped' : ''}`}
          onClick={() => handleFlip(index)}
        >
          <div className="card-inner">
            <div className="card-front">
              <h2>{content.front.title}</h2>
              <p>{content.front.text}</p>
            </div>
            <div className="card-back">
              <h2>{content.back.title}</h2>
              <p>{content.back.text}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CardComponent;
