import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [cards, setCards] = useState([]);
  const [newCard, setNewCard] = useState({ Question: '', Answer: '' });
  const [editingCard, setEditingCard] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [flipped, setFlipped] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5000/cards')
      .then(response => {
        setCards(response.data);
        setFlipped(new Array(response.data.length).fill(false));
        setCurrentIndex(response.data.length > 0 ? 0 : null);
      })
      .catch(error => {
        console.error('There was an error fetching the cards!', error);
      });
  }, []);

  const handleAddCard = () => {
    axios.post('http://localhost:5000/cards', newCard)
      .then(response => {
        const updatedCards = [...cards, { ...newCard, id: response.data.id }];
        setCards(updatedCards);
        setNewCard({ Question: '', Answer: '' });
        setShowAddForm(false);
        setCurrentIndex(updatedCards.length - 1); // Show the new card
      })
      .catch(error => {
        console.error('There was an error adding the card!', error);
      });
  };

  const handleDeleteCard = (id) => {
    axios.delete(`http://localhost:5000/cards/${id}`)
      .then(() => {
        const updatedCards = cards.filter(card => card.id !== id);
        setCards(updatedCards);
        setCurrentIndex(updatedCards.length > 0 ? Math.max(0, currentIndex - 1) : null);
      })
      .catch(error => {
        console.error('There was an error deleting the card!', error);
      });
  };

  const handleEditCard = () => {
    axios.put(`http://localhost:5000/cards/${editingCard.id}`, editingCard)
      .then(() => {
        setCards(cards.map(card => (card.id === editingCard.id ? editingCard : card)));
        setEditingCard(null);
      })
      .catch(error => {
        console.error('There was an error updating the card!', error);
      });
  };

  const handleFlip = (index) => {
    const newFlipped = [...flipped];
    newFlipped[index] = !newFlipped[index];
    setFlipped(newFlipped);
  };

  const handleNext = () => {
    if (cards.length > 0) {
      setCurrentIndex((currentIndex + 1) % cards.length);
    }
  };

  const handlePrevious = () => {
    if (cards.length > 0) {
      setCurrentIndex((currentIndex - 1 + cards.length) % cards.length);
    }
  };

  if (cards.length === 0) return <p>No cards available</p>;

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      {currentIndex !== null && cards.length > 0 && (
        <div className="card-container">
          <div
            key={cards[currentIndex].id}
            className={`card ${flipped[currentIndex] ? 'flipped' : ''}`}
            onClick={() => handleFlip(currentIndex)}
          >
            <div className="card-inner">
              <div className="card-front">
                <h2>{cards[currentIndex].Question}</h2>
              </div>
              <div className="card-back">
                <h2>{cards[currentIndex].Answer}</h2>
              </div>
            </div>
            <div className="card-actions">
              <button className="delete-button" onClick={(e) => {
                e.stopPropagation();
                handleDeleteCard(cards[currentIndex].id);
              }}>Delete</button>
              <button className="edit-button" onClick={(e) => {
                e.stopPropagation();
                setEditingCard(cards[currentIndex]);
              }}>Edit</button>
            </div>
            {editingCard && editingCard.id === cards[currentIndex].id && (
              <div className="edit-form">
                <input
                  type="text"
                  value={editingCard.Question}
                  onChange={e => setEditingCard({ ...editingCard, Question: e.target.value })}
                />
                <input
                  type="text"
                  value={editingCard.Answer}
                  onChange={e => setEditingCard({ ...editingCard, Answer: e.target.value })}
                />
                <button onClick={handleEditCard}>Save Changes</button>
                <button onClick={() => setEditingCard(null)}>Cancel</button>
              </div>
            )}
          </div>
        </div>
      )}
      <div className="navigation-buttons">
        <button className="nav-button" onClick={handlePrevious} disabled={cards.length === 0}>Previous</button>
        <button className="nav-button" onClick={handleNext} disabled={cards.length === 0}>Next</button>
      </div>
      <div className="add-card">
        {!showAddForm ? (
          <button className="add-card-button" onClick={() => setShowAddForm(true)}>Add New Card</button>
        ) : (
          <div className="add-card-form">
            <h2>Add New Card</h2>
            <input
              type="text"
              placeholder="Question"
              value={newCard.Question}
              onChange={e => setNewCard({ ...newCard, Question: e.target.value })}
            />
            <input
              type="text"
              placeholder="Answer"
              value={newCard.Answer}
              onChange={e => setNewCard({ ...newCard, Answer: e.target.value })}
            />
            <button onClick={handleAddCard}>Add Card</button>
            <button onClick={() => setShowAddForm(false)}>Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
