import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [cards, setCards] = useState([]);
  const [newCard, setNewCard] = useState({ Question: '', Answer: '' });
  const [editingCard, setEditingCard] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [flipped, setFlipped] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/cards')
      .then(response => {
        setCards(response.data);
        setFlipped(new Array(response.data.length).fill(false));
      })
      .catch(error => {
        console.error('There was an error fetching the cards!', error);
      });
  }, []);

  const handleAddCard = () => {
    axios.post('http://localhost:5000/cards', newCard)
      .then(response => {
        setCards([...cards, { ...newCard, id: response.data.id }]);
        setNewCard({ Question: '', Answer: '' });
        setShowAddForm(false);
      })
      .catch(error => {
        console.error('There was an error adding the card!', error);
      });
  };

  const handleDeleteCard = (id) => {
    axios.delete(`http://localhost:5000/cards/${id}`)
      .then(() => {
        setCards(cards.filter(card => card.id !== id));
      })
      .catch(error => {
        console.error('There was an error deleting the card!', error);
      });
  };

  const handleEditCard = () => {
    axios.put(`http://localhost:5000/cards/${editingCard.id}`, editingCard)
      .then(() => {
        setCards(cards.map(card => (card.id === editingCard.id ? editingCard : card)));
        setEditingCard(null);
      })
      .catch(error => {
        console.error('There was an error updating the card!', error);
      });
  };

  const handleFlip = (index) => {
    const newFlipped = [...flipped];
    newFlipped[index] = !newFlipped[index];
    setFlipped(newFlipped);
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      <div className="card-container">
        {cards.map((card, index) => (
          <div
            key={card.id}
            className={`card ${flipped[index] ? 'flipped' : ''}`}
            onClick={() => handleFlip(index)}
          >
            <div className="card-inner">
              <div className="card-front">
                <h2>{card.Question}</h2>
              </div>
              <div className="card-back">
                <h2>{card.Answer}</h2>
              </div>
            </div>
            <div className="card-actions">
              <button onClick={() => handleDeleteCard(card.id)}>Delete</button>
              <button onClick={() => setEditingCard(card)}>Edit</button>
            </div>
          </div>
        ))}
      </div>
      <div className="add-card">
        <button className="add-card-button" onClick={() => setShowAddForm(true)}>Add New Card</button>
        {showAddForm && (
          <div className="add-card-form">
            <h2>{editingCard ? 'Edit Card' : 'Add New Card'}</h2>
            <input
              type="text"
              placeholder="Question"
              value={editingCard ? editingCard.Question : newCard.Question}
              onChange={e =>
                editingCard
                  ? setEditingCard({ ...editingCard, Question: e.target.value })
                  : setNewCard({ ...newCard, Question: e.target.value })
              }
            />
            <input
              type="text"
              placeholder="Answer"
              value={editingCard ? editingCard.Answer : newCard.Answer}
              onChange={e =>
                editingCard
                  ? setEditingCard({ ...editingCard, Answer: e.target.value })
                  : setNewCard({ ...newCard, Answer: e.target.value })
              }
            />
            <button onClick={editingCard ? handleEditCard : handleAddCard}>
              {editingCard ? 'Save Changes' : 'Add Card'}
            </button>
            <button onClick={() => {
              setEditingCard(null);
              setShowAddForm(false);
            }}>Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
