import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [cards, setCards] = useState([]);
  const [newCard, setNewCard] = useState({ Question: '', Answer: '' });
  const [editCard, setEditCard] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/cards')
      .then(response => {
        setCards(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the cards!', error);
      });
  }, []);

  const handleAddCard = () => {
    axios.post('http://localhost:5000/cards', newCard)
      .then(response => {
        setCards([...cards, { ...newCard, id: response.data.id }]);
        setNewCard({ Question: '', Answer: '' });
      })
      .catch(error => {
        console.error('There was an error adding the card!', error);
      });
  };

  const handleDeleteCard = (id) => {
    axios.delete(`http://localhost:5000/cards/${id}`)
      .then(() => {
        setCards(cards.filter(card => card.id !== id));
      })
      .catch(error => {
        console.error('There was an error deleting the card!', error);
      });
  };

  const handleEditCard = () => {
    axios.put(`http://localhost:5000/cards/${editCard.id}`, editCard)
      .then(() => {
        setCards(cards.map(card => (card.id === editCard.id ? editCard : card)));
        setEditCard(null);
      })
      .catch(error => {
        console.error('There was an error updating the card!', error);
      });
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      <div className="card-list">
        {cards.map(card => (
          <div key={card.id} className="card-item">
            <h2>{card.Question}</h2>
            <p>{card.Answer}</p>
            <button onClick={() => handleDeleteCard(card.id)}>Delete</button>
            <button onClick={() => setEditCard(card)}>Edit</button>
          </div>
        ))}
      </div>
      {editCard && (
        <div className="edit-card">
          <h2>Edit Card</h2>
          <input
            type="text"
            value={editCard.Question}
            onChange={e => setEditCard({ ...editCard, Question: e.target.value })}
          />
          <input
            type="text"
            value={editCard.Answer}
            onChange={e => setEditCard({ ...editCard, Answer: e.target.value })}
          />
          <button onClick={handleEditCard}>Save</button>
        </div>
      )}
      <div className="add-card">
        <h2>Add New Card</h2>
        <input
          type="text"
          placeholder="Question"
          value={newCard.Question}
          onChange={e => setNewCard({ ...newCard, Question: e.target.value })}
        />
        <input
          type="text"
          placeholder="Answer"
          value={newCard.Answer}
          onChange={e => setNewCard({ ...newCard, Answer: e.target.value })}
        />
        <button onClick={handleAddCard}>Add Card</button>
      </div>
    </div>
  );
};

export default AdminDashboard;
